$ for i in *.vcf.gz; do echo $i; samplename=`echo $i | awk -F "_" '{print $1}'`; bedtools intersect -a $i -b somatic-hg38-1000g_pon.hg38.vcf.gz -header | bcftools view -i "FORMAT/DP>99" - | grep -E '0/1:' > tmp.txt && paste <(awk -F "\t" '{print $1":"$2}' tmp.txt) <(awk -F "\t" '{print $10}' tmp.txt | awk -F ":" '{print $3}')  > ${samplename}_pon_highdp_afs.txt; rm tmp.txt ;  done
